import React, { useState, useEffect } from "react";
import Div from "./components/Div";
import Form from "./components/Form";
import Header from "./components/Header";
import Title from "./components/Title";
import Wrapper from "./components/Wrapper";


function App(props) {

  const [buscar, setBuscar] = React.useState(true);
  const [Page, setPage] = React.useState(undefined);

  useEffect(() => {

    const page = 
    <div style = {{      
    margin: "auto",
    marginTop: 50,
    backgroundColor: "#000003",
    width: 400,
    height: 400,
    borderRadius: 10,
  }}>
      <img style = {{marginTop:50, width: 300,  height: 300, borderRadius: 10}} src="https://media.istockphoto.com/vectors/abstract-stipple-background-blue-luminous-circles-circular-ring-light-vector-id1191834647?k=6&m=1191834647&s=170667a&w=0&h=2_KIHnvfDPxNPz0Jab-G7sVEAfLYu8lDsVQzXIQS6VY=" alt= "logo botTube"/>
    </div>

    const page2 = <>
    <img style = {{width: 400, height: 400, marginTop: 50, borderRadius: 10}} src="https://i.pinimg.com/originals/a0/11/c5/a011c5c408f24126c07f599e190ad78d.gif" alt= "logo botTube"/>
    </>
    if(buscar == false){
      setPage(page)
    }else{setPage(page2)}

  },[]);

  function loadDoc(){
    console.log("AAMAMAMAMA");
  }

  return(

      <Header>
        <div>
          <a href= "https://youtube.com">
          <img style = {{width: 120, height: 80, borderRadius: 30}} src="https://yt3.ggpht.com/ytc/AAUvwngR3ZPbDMQjeGv4VCMagBrRgL0qpQXJhe9jOxLZuA=s900-c-k-c0x00ffffff-no-rj" alt= "logo botTube"/>
          </a>
        </div>
    <Wrapper>
      <Title  >
        <h1>BotTube</h1>
      </Title>
      <Form>

        <>{ Page }
        <button type="button" onClick={loadDoc}>Buscar</button>
        </>

      </Form>
      <Div>
      <p style={{marginBottom:15}}>
Consultas de músicas no YouTube © 2021</p>
      </Div>
    </Wrapper>
      </Header>
    ) 




}

export default App;
