import React, { useState, useEffect } from "react";
import Button from "./components/Button";
import {DivImg1, DivImg2} from "./components/DivImg";
import Form from "./components/Form";
import Input from "./components/Input";
import Title from "./components/Title";
import Wrapper from "./components/Wrapper";
import puppeteer from "./services/puppeteer";
import img from "./assets/img.svg";
import Div from "./components/Div";



function App() {

  const [buscar, setBuscar] = React.useState(1);
  const [ musicUrl,  setMusicUrl ] = React.useState("");
  const [history, setHistory] = React.useState([]);


  useEffect(() => {



  },[]);


  const loadDoc = async () => {
    setBuscar(2)
    const live = await puppeteer();
    if(sessionStorage.getItem("@botData")){
      setHistory(JSON.parse(sessionStorage.getItem("@botData")))
    }
    setBuscar(3)
    setMusicUrl(live.url)
    console.log( "mano",live)
  }

  return(


    <Wrapper>
        <div>
          <a href= "https://youtube.com">
            <img style = {{padding:20, width: 120, height: 80, borderRadius: 30}} 
            src="https://yt3.ggpht.com/ytc/AAUvwngR3ZPbDMQjeGv4VCMagBrRgL0qpQXJhe9jOxLZuA=s900-c-k-c0x00ffffff-no-rj" alt= "logo botTube"/>
          </a>
        </div>
      <Title  >
        <h1>BotTube</h1>
      </Title>
      <Form>
       <>{ buscar == 1 ? <DivImg1/> : buscar == 2 ? <DivImg2/> : 
        <DivImg2>
          <iframe id="ytplayer" type="text/html" width="100%" height="100%" src={musicUrl}frameborder="0"/>
        </DivImg2>}        

        <div style={{display:"flex", flexDirection:"row"}}>
          <Input placeholder= "Digite o nome da música"></Input>
          <Button 
            type="button" 
            onClick={loadDoc}>
              <img style = {{width: "100%", padding: 5, height: "80%", borderRadius: 100}} src={img} />
          </Button>
        </div>
        </>

      {history.length > 0 ?

        <Div>
          <Title>
            Histórico
            <Div/>
          </Title>

        </Div>
      :<></>}
      </Form>

    </Wrapper>
    ) 




}

export default App;
