import React, { useState, useEffect } from "react";
import Button from "./components/Button";
import {DivImg1, DivImg2} from "./components/DivImg";
import Form from "./components/Form";
import Input from "./components/Input";
import Title from "./components/Title";
import Wrapper from "./components/Wrapper";
import puppeteer from "./services/puppeteer";
import img from "./assets/img.svg";



function App() {

  const [buscar, setBuscar] = React.useState(false);
  const [Page, setPage] = React.useState(undefined);

  useEffect(() => {

    console.log(window.innerWidth)


  },[]);


  const loadDoc = async () => {
    setBuscar(true)
    const live = await puppeteer();

    console.log( "mano",live)
  }

  return(


    <Wrapper>
        <div>
          <a href= "https://youtube.com">
          <img style = {{padding:20, width: 120, height: 80, borderRadius: 30}} src="https://yt3.ggpht.com/ytc/AAUvwngR3ZPbDMQjeGv4VCMagBrRgL0qpQXJhe9jOxLZuA=s900-c-k-c0x00ffffff-no-rj" alt= "logo botTube"/>
          </a>
        </div>
      <Title  >
        <h1>BotTube</h1>
      </Title>
      <Form>





        <>{ buscar == false ? <DivImg1/> : <DivImg2>      <iframe id="ytplayer" type="text/html" width="100%" height="100%"
  src="https://www.youtube.com/embed/3vYeQLJ2as4?autoplay=1"
  frameborder="0"/></DivImg2>}
        <div style={{display:"flex", flexDirection:"row"}}>
        <Input placeholder= "Digite o nome da música"></Input>
        <Button style={{marginBottom:310}} type="button" onClick={loadDoc}><img style = {{width: 90, padding: 5, height: 50, borderRadius: 100}} src={img} /></Button>
        </div>
        </>
      </Form>
      <p>Consultas de músicas no YouTube © 2021</p>
    </Wrapper>
    ) 




}

export default App;
