import styled, { keyframes } from 'styled-components';
const animation = keyframes`  
  from { opacity: 0; }
  to { opacity: 1; }
  to { opacity: 0; }
`;
const FadeIn = styled.div`
 animation: ${animation} 3s;
 animation-iteration-count: infinite;   
`;
export default FadeIn;
